import httpxfrom telethon import eventsIP_USAGE_ERROR = 'âŒ Ğ˜ÑĞ¿Ğ¾Ğ»ÑŒĞ·Ğ¾Ğ²Ğ°Ñ‚ÑŒ Ñ IP! \nĞŸÑ€Ğ¸Ğ¼ĞµÑ€:\n.ip 123.456.789.021'IP_NOT_FOUND_ERROR = 'âŒ IP Ğ½Ğµ Ğ½Ğ°Ğ¹Ğ´ĞµĞ½ Ğ¸Ğ»Ğ¸ Ğ½ĞµĞ´Ğ¾ÑÑ‚ÑƒĞ¿ĞµĞ½!'IP_INFO_TEMPLATE = (    "âš¡ï¸ Ğ¦ĞµĞ»ÑŒ: {ip}"    "\n\nğŸ—„ ĞŸÑ€Ğ¾Ğ²Ğ°Ğ¹Ğ´ĞµÑ€ (ISP): {isp}"    "\nğŸ‘¥ ĞÑ€Ğ³Ğ°Ğ½Ğ¸Ğ·Ğ°Ñ†Ğ¸Ñ: {org}"    "\nğŸ´ Ğ¡Ñ‚Ñ€Ğ°Ğ½Ğ°: {country} ({countryCode})"    "\nğŸ“Œ Ğ ĞµĞ³Ğ¸Ğ¾Ğ½: {regionName} ({region})"    "\nğŸŒ‡ Ğ“Ğ¾Ñ€Ğ¾Ğ´: {city}"    "\nğŸ¤ ĞŸĞ¾Ñ‡Ñ‚Ğ¾Ğ²Ñ‹Ğ¹ Ğ¸Ğ½Ğ´ĞµĞºÑ: {zip}"    "\nğŸ•’ Ğ§Ğ°ÑĞ¾Ğ²Ğ¾Ğ¹ Ğ¿Ğ¾ÑÑ: {timezone}"    "\nğŸ“ Ğ¨Ğ¸Ñ€Ğ¾Ñ‚Ğ°: {lat}"    "\nğŸ“ Ğ”Ğ¾Ğ»Ğ³Ğ¾Ñ‚Ğ°: {lon}"    "\nğŸ› AS: {as_}")def a(client):    @client.on(events.NewMessage(pattern=r"\.ip", outgoing=True))    async def _(event):        args = event.text.split()        if len(args) < 2:            await event.edit(IP_USAGE_ERROR, parse_mode='html')            return        ip = args[-1]        try:            async with httpx.AsyncClient(timeout=5) as client_http:                response = await client_http.get(f"https://ip-api.com/json/{ip}")                data = response.json()            if data.get('status') != 'success':                await event.edit(IP_NOT_FOUND_ERROR, parse_mode='html')                return            await event.edit(                IP_INFO_TEMPLATE.format(                    ip=ip,                    isp=data.get('isp', 'N/A'),                    org=data.get('org', 'N/A'),                    country=data.get('country', 'N/A'),                    countryCode=data.get('countryCode', 'N/A'),                    regionName=data.get('regionName', 'N/A'),                    region=data.get('region', 'N/A'),                    city=data.get('city', 'N/A'),                    zip=data.get('zip', 'N/A'),                    timezone=data.get('timezone', 'N/A'),                    lat=data.get('lat', 'N/A'),                    lon=data.get('lon', 'N/A'),                    as_=data.get('as', 'N/A')                ),                parse_mode='html'            )        except httpx.RequestError:            await event.edit(IP_NOT_FOUND_ERROR, parse_mode='html')